import React, { useContext, useEffect, useState } from "react";

export const timeValue = React.createContext()

const SetTimer = () => {
    const [[m,s],setTime] = useState(timeValue.value)
    console.log([m,s])

    const saveTime = () => {
        timeValue.value = [m,s];
    }

    const changeMin = (e) => {
        setTime([e.target.value,s]);
    }

    function changeSec (e) {
        setTime([m, e.target.value]);
    }

    return (
        <>

            <input onChange={changeMin} type="number" min='0' max='59' />
            <input onChange={changeSec} type="number" min='1' max='59' />
            <button onClick={saveTime}>Сохранить время на ход</button>
            <p>Время на ход : {timeValue.value[0]} минут {timeValue.value[1]} секунд</p>
        </>
    )
}

export default SetTimer
