import React, { useContext, useState } from "react";

const timeValue = React.createContext()

const SetTimer = () => {
    timeValue.value = [1,1]
    const [[m,s],setTime] = useState(useContext(timeValue.value))

    const saveTime = () => {
        timeValue.value = [m,s] 
    }

    const changeMin = () => {
        setTime([this.value,s]);
    }

    const changeSec = () => {
        setTime([m,this.value]);
    }

    return (
        <>
            <input value={1} onChange={changeMin}  type="number" min='0' max='59' />
            <input value={1} onChange={changeSec}  type="number" min='1' max='59' />
            <button onClick={saveTime}>Сохранить время на ход</button>
            <p>Время на ход : {m} минут {s} секунд</p>
        </>
    )
}

export default SetTimer
