import React, { useEffect, useState } from "react";

export const timeValue = React.createContext()

const SetTimer = () => {
    const [[m,s],setTime] = useState([1,0])
    useEffect(() => {
        timeValue.value = [m,s];
    },[m,s])

    const saveTime = () => {
        timeValue.value = [m,s];
        console.log(timeValue.value)
    }

    const changeMin = (e) => {
        setTime([Number(e.target.value),s]);
    }

    function changeSec (e) {
        setTime([m, Number(e.target.value)]);
    }

    return (
        <>
            <timeValue.Provider value={[m,s]}>
                <input onChange={changeMin} type="number" min='0' max='59' />
                <input onChange={changeSec} type="number" min='1' max='59' />
                <button onClick={saveTime}>Сохранить время на ход</button>
                <p>Время на ход : {m} минут {s} секунд</p>
            </timeValue.Provider>
        </>
    )
}

export default SetTimer
